import { required, email, minLength } from "vuelidate/lib/validators";
import { USER, PASS } from "../API_KEY";
export default {
  name: "briefingform",
  mixins: [validationMixin],
  data: function data() {
    return {
      isLogin: true,
      user_name: null,
      lines: [],
      multiple: null,
      dataUriForFiles: "",
      attachName: "",
      blockRemoval: true,
      assetList: "",
      addressList: "",
      path: "",
      form: {
        requester: "",
        email: "",
        advertiser: "",
        campaignName: "",
        startDate: "",
        endDate: "",
        currency: "PHP",
        budget: "",
        objective: "",
        target: "",
        material: "",
        competitors: "",
        additional: "",
        assets: "",
        address: "",
        rowData: []
      },
      userSaved: false,
      sending: false,
      buttonMsg: "Submit Request"
    };
  },
  validations: {
    form: {
      requester: {
        required: required,
        minLength: minLength(3)
      },
      email: {
        required: required,
        email: email
      },
      advertiser: {
        required: required,
        maxLength: minLength(3)
      },
      campaignName: {
        required: required
