      endDate: {
        required: required
      },
      currency: {
        required: required
      },
      budget: {
        required: required
      },
      objective: {
        required: required
      },
      target: {
        required: required
      },
      competitors: {
        required: required
      },
      material: {
        required: required
      }
    }
  },
  methods: {
    getValidationClass: function getValidationClass(fieldName) {
      var field = this.$v.form[fieldName];

      if (field) {
        return {
          "md-invalid": field.$invalid && field.$dirty
        };
      }
    },
    // To add line for digital assets
    addLine: function addLine() {
      var my_object = {
        asset: this.form.assets,
        address: this.form.address
      };
      this.form.rowData.push(my_object);
      this.form.assets = "";
      this.form.address = "";
    },
    // Delete line in digital assets line
    deleteLine: function deleteLine(lineId) {
      this.form.rowData.splice(lineId, 1);
    },
    // To validate Form which is required
    validateForm: function validateForm() {
      this.$v.$touch();

      if (!this.$v.$invalid) {
        this.saveDigitalAssets();
        this.fixDate();
        this.makePDF();
      }
    },
    // Sending an email using SmtpJS
    sendForm: function sendForm(Data) {
      var _this = this;

      // add <br> for spacing with enter key
      this.form.objective = this.form.objective.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.form.target = this.form.target.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.form.competitors = this.form.competitors.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.form.material = this.form.material.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.form.additional = this.form.additional.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.assetList = this.assetList.replace(/(?:\r\n|\r|\n)/g, "<br>");
      this.addressList = this.addressList.replace(/(?:\r\n|\r|\n)/g, "<br>");
      var self = this;
      this.sending = true;
      var dataUri = "data:application/pdf;base64,".concat(Data);
      Email.send({
        Host: "smtp.gmail.com",
        Username: USER,
        Password: PASS,
        To: "yohan.choi@davidandgolyat.com",
        Cc: this.form.email,
        From: USER,
        Subject: "Request from ".concat(this.user_name),
        Body: "<div style='overflow-x: auto'>\n          <h1 align='center' style='color: black'>BRIEFING FORM</h1>\n          <table align='center' border='1' cellpadding='5' cellspacing='0' width='100%'\n          style='font-family: Helvetica; color: #000000; border-collapse: collapse; text-align: center; border-color: black; vertical-align:middle;'>\n          <tr>\n          <th colspan='4' bgcolor='#000000' style='color: white'>REQUEST OVERVIEW</th>\n          </tr>\n          <tr>\n          <td rowspan='3' width='25%'>CLIENT INFORMATION</td>\n          <td width='20%'>REQUESTED BY</td>\n          <td colspan='2' width='55%'>\n          ".concat(this.form.requester, "\n          </td>\n          </tr>\n          <tr>\n          <td>E-MAIL</td>\n          <td colspan='2'>\n          ").concat(this.form.email, "\n          </td>\n          </tr>\n          <tr>\n          <td>ADVERTISER</td>\n          <td colspan='2'>\n          ").concat(this.form.advertiser, "\n          </td>\n          </tr>\n          <tr>\n          <td rowspan='5' bgcolor='Black' style='color: white;'>CAMPAIGN AND BILLING INFORMATION</td>\n          <td rowspan='2'>CAMPAIGN</td>\n          <td width='15%'>NAME</td>\n          <td width='40%'>\n          ").concat(this.form.campaignName, "\n          </td>\n          </tr>\n          <tr>\n          <td>OBJECTIVE</td>\n          <td align='justify'>\n          ").concat(this.form.objective, "\n          </td>\n          </tr>\n          <tr>\n          <td rowspan='3'>BUDGET AND PERIOD</td>\n          <td>\n          ").concat(this.form.currency, "\n          </td>\n          <td>\n          ").concat(this.form.budget, "\n          </td>\n          </tr>\n          <tr>\n          <td>START DATE</td>\n          <td>\n          ").concat(this.form.startDate, "\n          </td>\n          </tr>\n          <tr>\n          <td>END DATE</td>\n          <td>\n          ").concat(this.form.endDate, "\n          </td>\n          </tr>\n          <tr>\n          <td rowspan='4'>MARKET INFORMATION</td>\n          <td>TARGET MARKET</td>\n          <td colspan='2' width='55%' align='justify'>\n          ").concat(this.form.target, "\n          </td>\n          </tr>\n          <tr>\n          <td id='second'>COMPETITORS</td>\n          <td colspan='2' align='justify'>\n          ").concat(this.form.competitors, "\n          </td>\n          </tr>\n          <tr>\n          <td>MATERIALS</td>\n          <td colspan='2' align='justify'>\n          ").concat(this.form.material, "\n          </td>\n          </tr>\n          <tr>\n          <td>ADDITIONAL INFO</td>\n          <td colspan='2' align='justify'>\n          ").concat(this.form.additional, "\n          </td>\n          </tr>\n          <tr>\n          <td bgcolor='Black' style='color: white'>DIGITAL ASSETS</td>\n          <td>\n          ").concat(this.assetList, "\n          </td>\n          <td colspan='2'>\n          ").concat(this.addressList, "\n          </td>\n          </tr>\n          </table>\n          </div>"),
        Attachments: [{
          name: "Briefing Form_".concat(this.user_name, ".pdf"),
          data: dataUri
        }, {
          name: this.attachName,
          data: this.dataUriForFiles
        }]
      }).then(function (message) {
        console.log(message);

        _this.clearForm();

        _this.$router.push({
          name: "thankyou"
        });
      });
    },
    //Make PDF File(JS)
    makePDF: function makePDF() {
      var fs = require("fs");

      var pdfMake = require("pdfmake/build/pdfmake.js");

      this.buttonMsg = "Sending...Please Wait";

      if (pdfMake.vfs == undefined) {
        var pdfFonts = require("pdfmake/build/vfs_fonts.js");

        pdfMake.vfs = pdfFonts.pdfMake.vfs;
      }

      var docDefinition = {
        content: [{
          text: "BRIEFING FORM OF ".concat(this.user_name),
          style: "header",
          alignment: "center"
        }, {
          table: {
            widths: ["25%", "20%", "15%", "40%"],
            headerRows: 1,
            body: [// First Row
            [{
              text: "REQUEST OVERVIEW",
              style: "tableHeader",
              colSpan: 4,
              alignment: "center"
            }, "", "", ""], // Client Information part
            [{
              rowSpan: 3,
              text: "CLIENT INFORMATION",
              alignment: "center"
            }, {
              text: "REQUESTED BY",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.requester,
              alignment: "center"
            }, ""], ["", {
              text: "E-MAIL",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.email,
              alignment: "center"
            }, ""], ["", {
              text: "ADVERTISER",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.advertiser,
              alignment: "center"
            }, ""], // Campaign and billing Information part
            [{
              rowSpan: 5,
              text: "CAMPAIGN AND BILLING INFORMATION",
              alignment: "center"
            }, {
              rowSpan: 2,
              text: "CAMPAIGN",
              alignment: "center"
            }, {
              text: "NAME",
              alignment: "center"
            }, {
              text: this.form.campaignName,
              alignment: "center"
            }], ["", "", {
              text: "OBJECTIVE",
              alignment: "center"
            }, {
              text: this.form.objective,
              alignment: "justify"
            }], ["", {
              rowSpan: 3,
              text: "BUDGET AND PERIOD",
              alignment: "center"
            }, {
              text: this.form.currency,
              alignment: "center"
            }, {
              text: this.form.budget,
              alignment: "center"
            }], ["", "", {
              text: "START DATE",
              alignment: "center"
            }, {
              text: this.form.startDate,
              alignment: "center"
            }], ["", "", {
              text: "END DATE",
              alignment: "center"
            }, {
              text: this.form.endDate,
              alignment: "center"
            }], // Market Information Part
            [{
              rowSpan: 4,
              text: "MARKET INFORMATION",
              alignment: "center"
            }, {
              text: "TARGET MARKET",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.target,
              alignment: "justify"
            }, ""], ["", {
              text: "COMPETITORS",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.competitors,
              alignment: "justify"
            }, ""], ["", {
              text: "MATERIALS",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.material,
              alignment: "justify"
            }, ""], ["", {
              text: "ADDITIONAL INFO",
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.form.additional,
              alignment: "justify"
            }, ""], // Digital Assets part
            [{
              text: "DIGITAL ASSETS",
              alignment: "center"
            }, {
              text: this.assetList,
              alignment: "center"
            }, {
              colSpan: 2,
              text: this.addressList,
              alignment: "center"
            }, ""]]
          }
        }],
        styles: {
          header: {
            fontSize: 18,
            bold: true,
            margin: [0, 0, 0, 10]
          }
        },
        defaultStyle: {}
      }; //pdfMake.createPdf(docDefinition).download("test.pdf");

      var pdf = pdfMake.createPdf(docDefinition);
      var pdfData;
      var self = this;
      pdf.getBase64(function (encodedString) {
        pdfData = encodedString;
        self.sendForm(pdfData);
      });
    },
    // To save Digital Assets for PDF form
    saveDigitalAssets: function saveDigitalAssets() {
      for (var item in this.form.rowData) {
        this.assetList += this.form.rowData[item].assets + "\n";
        this.addressList += this.form.rowData[item].address + "\n";
      }
    },
    // To fix date
    fixDate: function fixDate() {
      this.form.startDate = this.form.startDate.toString().substring(4, 15);
      this.form.endDate = this.form.endDate.toString().substring(4, 15);
    },
    // Clear Form after sending an email
    clearForm: function clearForm() {
      this.$v.$reset();
      this.form.requester = "";
      this.form.email = "";
      this.form.advertiser = "";
      this.form.campaignName = "";
      this.form.startDate = "";
      this.form.endDate = "";
      this.form.currency = "PHP";
      this.form.budget = "";
      this.form.objective = "";
      this.form.target = "";
      this.form.material = "";
      this.form.competitors = "";
      this.form.additional = "";
      this.form.assets = "";
      this.form.address = "";
      this.form.rowData = [];
    },
    // Make Zip file for multiple attachments
    onFileChange: function onFileChange() {
      var zip = new jsZip();
      var numberOfFiles = event.srcElement.files.length;
      var numberOfProcessedFiles = 0;
      var self = this;

      for (var i = 0; i < numberOfFiles; i++) {
        (function (i) {
          var file = event.srcElement.files[i];
          var reader = new FileReader();
          reader.readAsBinaryString(file);

          reader.onload = function () {
            numberOfProcessedFiles++;
            zip.file(file.name, btoa(reader.result), {
              base64: true
            });

            if (numberOfProcessedFiles) {
              zip.generateAsync({
                type: "base64"
              }).then(function (content) {
                self.dataUriForFiles = "data:application/x-zip-compressed;base64,".concat(content);
                self.attachName = "Attachment.zip";
              });
            }
          };
        })(i);
      }
    }
  },
  mounted: function mounted() {
    this.addLine();
    this.user_name = this.$route.params.code;
    console.log("Hello, ".concat(this.user_name)); // To prevent unauthorized approach

    if (this.user_name == null) {
      alert("Login Required.");
      this.$router.push("login");
    }
